import { GoogleGenAI } from "@google/genai";

const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

const response = await ai.models.generateContent({
  model: 'gemini-2.5-flash',
  contents: `Répondez uniquement par 'JUMP' ou 'WAIT'. État : Obstacle à 150px. Vélocité du joueur 0.`,
  config: {
    // Crucial pour le jeu en temps réel : désactiver la réflexion pour une réponse instantanée.
    thinkingConfig: { thinkingBudget: 0 }
  },
});

const action = response.text.trim(); // Retourne 'JUMP' ou 'WAIT'
