import { openDB } from 'idb';

const dbPromise = openDB('NoteTakerDB', 1, {
  upgrade(db) {
    db.createObjectStore('notes', { keyPath: 'id' });
    db.createObjectStore('projects', { keyPath: 'id' });
    db.createObjectStore('userSettings', { keyPath: 'id' });
  },
});

